 function ns_social_icons_get_social_bar($link = null, $title = null,  $imageurl = null) {

  $tend_tdsocialicons_class_options = get_option( 'tend_tdsocialicons_class_option_name' ); // Array of All Options
  $social_bar_disable_0 = $tend_tdsocialicons_class_options['social_bar_disable_0']; // Social bar (disable)
  $facebook_url_1 = $tend_tdsocialicons_class_options['facebook_url_1']; // Facebook url
  $enable_facebook_2 = $tend_tdsocialicons_class_options['enable_facebook_2']; // Enable Facebook
  $linkedin_3 = $tend_tdsocialicons_class_options['linkedin_3']; // Linkedin
  $enable_linkedin_4 = $tend_tdsocialicons_class_options['enable_linkedin_4']; // Enable Linkedin
  $twitter_5 = $tend_tdsocialicons_class_options['twitter_5']; // Twitter
  $enable_twitter_6 = $tend_tdsocialicons_class_options['enable_twitter_6']; // Enable Twitter
  $instagram_7 = $tend_tdsocialicons_class_options['instagram_7']; // Instagram
  $enable_instagram_8 = $tend_tdsocialicons_class_options['enable_instagram_8']; // Enable Instagram
  $dribbble_9 = $tend_tdsocialicons_class_options['dribbble_9']; // Dribbble
  $enable_dribbble_10 = $tend_tdsocialicons_class_options['enable_dribbble_10']; // Enable Dribbble
  $behance_11 = $tend_tdsocialicons_class_options['behance_11']; // Behance
  $enable_behance_12 = $tend_tdsocialicons_class_options['enable_behance_12']; // Enable Behance
	$googleplus_13 = $tend_tdsocialicons_class_options['googleplus_13']; // googleplus
	//$enable_googleplus_14 = $tend_tdsocialicons_class_options['enable_googleplus_14']; // Enable googleplus
	$enable_googleplus_14  = (isset($_GET['enable_googleplus_14'])) ? $_GET['enable_googleplus_14'] : 0; // Enable googleplus

	$link =  get_permalink();  // Get link
	$title =  get_the_title();  // Get title
	$thumb_id = get_post_thumbnail_id(); // Get thumbnail
	$thumb_url = wp_get_attachment_image_src($thumb_id, true);
	$imageurl = $imageurl ?: $thumb_url[0];
	$fb_sharer_url = "http://www.facebook.com/sharer/sharer.php?s=100&amp;p[url]={$link}&amp;p[images][0]={$imageurl}&amp;p[title]={$title}";
	$tweet = get_the_title();
	$twitter_url = "http://twitter.com/share?url={$link}&text={$tweet}";
	$plusone_url = "https://plus.google.com/share?url={$link}";

  $output_socialbar =  '
		<div class="nuno-sarmento-socialicons-nav__bar">
		   <nav style="display:'.$social_bar_disable_0.'; "  class="tend_tdsocialicons_social">
		  		 <ul class="ns__social_list">
		  				 <li style="display:'.$enable_facebook_2.'"><a href="'.$facebook_url_1.'/sharer.php?u=' . esc_url( get_permalink() ) . '&amp;t=' . $title . '" title="Share on Facebook." onclick="PopupCenter(\''. $fb_sharer_url .'\', \'600\', \'345\') ;return false;">' . esc_html__( 'Facebook', 'nuno-sarmento-socialicons' ) . '<i class="fa fa-facebook"></i></a></li>
		  				 <li style="display:'.$enable_twitter_6.'"><a href="'.$twitter_5.'/home/?status='. $tweet .'-' . esc_url( get_permalink() ) . '" title="Tweet this!" onclick="PopupCenter(\''. $twitter_url .'\', \'500\', \'250\') ;return false; ">' . esc_html__( 'Twitter', 'nuno-sarmento-socialicons' ) . '<i class="fa fa-twitter"></i></a></li>
							 <li style="display:'.$enable_googleplus_14.'"><a href="'.$googleplus_13.'/share?url=' . esc_url( get_permalink() ) . '"  onclick="PopupCenter(\''.$plusone_url.'\', \'500\', \'450\' , \'menubar=no\' , \'toolbar=no\', \'resizable=yes\' , \'scrollbars=yes\') ;return false;"> ' . esc_html__( 'Google+', 'nuno-sarmento-socialicons' ) . '<i class="fa fa-google-plus"></i></a></li>
							 <li style="display:'.$enable_instagram_8.'"><a href="'.$instagram_7.'">' . esc_html__( 'Instagram', 'nuno-sarmento-socialicons' ) . '<i class="fa fa-instagram"></i></a></li>
							 <li style="display:'.$enable_linkedin_4.'"><a href="'.$linkedin_3.'/shareArticle?mini=true&amp;title='. $title .'&amp;url=' . esc_url( get_permalink() ) . '" title="Share on LinkedIn">' . esc_html__( 'Linkedin', 'nuno-sarmento-socialicons' ) . '<i class="fa fa-linkedin"></i></a></li>
		  				 <li style="display:'.$enable_dribbble_10.'"><a href="'.$dribbble_9.'">' . esc_html__( 'Dribbble', 'nuno-sarmento-socialicons' ) . '<i class="fa fa-dribbble"></i></a></li>
		  				 <li style="display:'.$enable_behance_12.'"><a href="'.$behance_11.'">' . esc_html__( 'Behance', 'nuno-sarmento-socialicons' ) . '<i class="fa fa-behance"></i></a></li>
		  		 </ul>
		   </nav>
	  </div>
	 ';

  echo $output_socialbar ;

 }
